import { useReducer } from "react";
import CartContext from "./cartcontent";

const defCart = { items: [], totalAmount: 0 };

const cartReducer = (state, action) => {
  if (action.type === "ADD") {
    const updItems = state.items.concat(action.item);
    const amount = state.totalAmount + action.item.price * action.item.amount;
    return {
      items: updItems,
      totalAmount: amount,
    };
  }
  return defCart;
};

const CartProvider = (props) => {
  const [cartState, CartAction] = useReducer(cartReducer, defCart);

  const addItemToCart = (item) => {
    CartAction({ type: "ADD", item: item });
  };

  const removeItemFromCart = (id) => {
    CartAction({ type: "REMOVE", id: id });
  };

  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemToCart,
    removeItem: removeItemFromCart,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
